import React from 'react';
import backgroundImage from '../images/maksym-kaharlytskyi-kDVaFjoQf4M-unsplash.jpg';
import { faCircleArrowRight } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { Link } from 'react-router-dom';

const FrontPage = () => {
  return (
    <section className="relative bg-center bg-no-repeat bg-cover h-[93vh]" style={{backgroundImage: `url(${backgroundImage})`}}>
      <div className="bg-gradient-to-r from-blue-900 via-purple-700 to-pink-500 bg-opacity-70 h-full flex items-center">
        <div className="px-4 mx-auto max-w-screen-xl text-left py-32 animate-fadeIn">
          <div className="max-w-2xl space-y-6">
            <h1 className="text-5xl md:text-6xl lg:text-7xl font-extrabold leading-none text-white uppercase animate-slideIn">
              Shipping.
            </h1>
            <h1 className="text-5xl md:text-6xl lg:text-7xl font-extrabold leading-none text-white uppercase animate-slideIn">
              Logistics.
            </h1>
            <h1 className="text-5xl md:text-6xl lg:text-7xl font-extrabold leading-none text-white uppercase animate-slideIn">
              Oil<span className="ml-5 tracking-tighter">Trading.</span>
            </h1>
            <p className="text-lg lg:text-xl text-gray-200 lg:px-48">
              Welcome to True Holding LLC, your reliable partner in logistics, ship chartering, and oil trading.
            </p>
          </div>
          <div className="flex flex-row mt-12 space-x-6">
            <Link to="/trading" className="flex items-center bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-full transition duration-300 shadow-lg animate-bounceIn">
              Trading
              <FontAwesomeIcon className='text-white text-xl ml-2' icon={faCircleArrowRight} />
            </Link>
            <Link to="/about" className="flex items-center bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-full transition duration-300 shadow-lg animate-bounceIn">
              About Us
              <FontAwesomeIcon className='text-white text-xl ml-2' icon={faCircleArrowRight} />
            </Link>
          </div>
        </div>
      </div>
    </section>
  );
};

export default FrontPage;
