import React from 'react';
import CargoShip from '../images/grant-durr-UPm28si8BRE-unsplash.jpg';
import OilImage from '../images/korie-jenkins-rVPxgFMFpdc-unsplash.jpg';

const Content = () => {  
    return (
        <section className="bg-gradient-to-r from-purple-400 via-pink-500 to-red-500 py-16">
            <div className="gap-16 items-center px-4 mx-auto max-w-screen-xl lg:grid lg:grid-cols-2 lg:px-6">
                <div className="text-white sm:text-lg">
                    <h2 className="mb-4 text-5xl tracking-tight font-extrabold animate-fadeIn">We Reinvented The Wheel</h2>
                    <p className="mb-4 mt-14 animate-fadeIn delay-500">At True Holding LLC, we are more than just an oil and gas company. We are architects of energy, visionaries who understand that the worldâ€™s progress hinges on reliable, sustainable energy sources. Our journey began with a commitment to excellence, and today, we stand as a beacon of innovation and reliability in the industry.</p>
                    <p className="animate-fadeIn delay-700">We are strategists, designers, and developers. Innovators and problem solvers. Simple and quick.</p>
                </div>
                <div className="grid grid-cols-2 gap-4 mt-8">
                    <img className="w-full rounded-lg border-4 border-white transform transition duration-500 hover:scale-105 animate-fadeIn delay-1000" src={CargoShip} alt="Cargo Ship"/>
                    <img className="mt-4 w-full lg:mt-10 rounded-lg border-4 border-white transform transition duration-500 hover:scale-105 animate-fadeIn delay-1200" src={OilImage} alt="Oil"/>
                </div>
            </div>
        </section>
    );
};

export default Content;
