import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircleArrowLeft, faCircleArrowRight } from '@fortawesome/free-solid-svg-icons';
import Azerbaijan from '../NewImages/az-06-ai-brush-removebg-yj7e7jt5 (1).png';
import Russia from '../NewImages/RussiaMap.png';
import Turkey from '../NewImages/turkey-flag-map-removebg (1).png';
import UAE from '../NewImages/united-arab-emirates-flag-map-vector-design_939093-73-removebg (1).png';

const Carousel = () => {
  const dataSlider = [
    {
      image: Azerbaijan,
      country: 'Azerbaijan',
      color: '#ff0000',
      text: 'Located in the border of Europe and Asia, Azerbaijan plays a crucial role in the world of oil production. Not only is it historically known as an oil country, but it plays a big role in the operations of True Holding LLC.',
    },
    {
      image: Russia,
      country: 'Russia',
      color: '#00ff00',
      text: 'With a geographic position that transposes from Europe to the far east side of Europe, Russia is an important player in bridging the clients of oil.',
    },
    {
      image: Turkey,
      country: 'Turkey',
      color: '#0000ff',
      text: 'Known historically as a vital country for trade due to its location on the silk road, Turkey has retained the same characteristic throughout centuries, connecting Asia and Europe and bringing in clients from all over the world.',
    },
    {
      image: UAE,
      country: 'UAE',
      color: '#800080',
      text: 'Country known for its oil production, favourable environment for businesses and all around a high efficient country UAE is not only a country known around the world but also its the headquarters of True Holding LLC.',
    },
  ];

  const [slide, setSlide] = useState(0);
  const length = dataSlider.length;

  const prevSlide = () => {
    setSlide(slide === 0 ? length - 1 : slide - 1);
  };

  const nextSlide = () => {
    setSlide(slide === length - 1 ? 0 : slide + 1);
  };

  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 5000);

    return () => clearInterval(interval);
  }, [slide]);

  return (
    <div className="bg-gray-200 min-h-[800px] flex items-center justify-center py-12">
      <div className="w-full max-w-screen-xl px-4">
        <div className="text-center mb-12">
          <hr className="border-gray-800 w-full mb-4" />
          <h1 className="text-3xl font-extrabold uppercase">- Our Service Area</h1>
        </div>
        <div className="relative flex items-center justify-center">
          <FontAwesomeIcon
            onClick={prevSlide}
            className="absolute left-0 ml-4 text-4xl text-black cursor-pointer hidden md:block"
            icon={faCircleArrowLeft}
          />
          <div className="flex items-center justify-center w-full bg-gray-800 rounded-xl shadow-lg h-auto max-w-3xl p-4 sm:p-8 md:p-12 lg:p-16 transition-transform transform hover:scale-105">
            <div className="flex flex-col md:flex-row items-center w-full">
              <div className="text-center md:text-left md:mr-8 mb-8 md:mb-0">
                <div className="text-3xl md:text-4xl lg:text-5xl font-bold text-white mb-6">{dataSlider[slide].country}</div>
                <p className="text-gray-400">{dataSlider[slide].text}</p>
              </div>
              {dataSlider.map((item, index) => (
                <div key={index} className={index === slide ? 'opacity-100' : 'opacity-0'}>
                  {index === slide && (
                    <img
                      className="rounded-lg max-w-full h-auto transition-opacity duration-500"
                      src={item.image}
                      alt={`Slide ${index}`}
                    />
                  )}
                </div>
              ))}
            </div>
          </div>
          <FontAwesomeIcon
            onClick={nextSlide}
            className="absolute right-0 mr-4 text-4xl text-black cursor-pointer hidden md:block"
            icon={faCircleArrowRight}
          />
        </div>
      </div>
    </div>
  );
}

export default Carousel;
